L = group L by &#39;Industry&#39;;
L = foreach L generate &#39;Industry&#39;,avg(&#39;Amount&#39;) as &#39;Amount&#39;, median(&#39;Amount&#39;) as &#39;Lmedian&#39;,avg(&#39;group_median&#39;) as &#39;group_median&#39;,median(avg(&#39;Amount&#39;)) over ( [..] partition by all ) as &#39;Lgroup_median&#39;;

H = filter q by &#39;Amount&#39; &gt; &#39;group_median&#39;;
