q = load &quot;simha__Wave_Sample_Opportunities3&quot;;

r = group q by &#39;Billing_Country&#39;;
r = foreach r generate sum(&#39;Amount&#39;) as &#39;Amount&#39;,first(&#39;Billing_Country&#39;) as &#39;Billing_Country&#39;;
r = group r by all;
r = foreach r generate stddevp(&#39;Amount&#39;) as &#39;stddevp_Amount&#39;,first(&#39;Billing_Country&#39;) as &#39;Billing_Country&#39;;

f = group q by &#39;Billing_Country&#39; left,r by &#39;Billing_Country&#39;;
f = foreach f generate q.&#39;Billing_Country&#39; as &#39;Billing_Country&#39;,sum(q.&#39;Amount&#39;) as &#39;Amount&#39;,max(r.&#39;stddevp_Amount&#39;) as &#39;stddev&#39;;
f = group f by &#39;Billing_Country&#39;;
f = foreach f generate avg(&#39;Amount&#39;) as &#39;Amount&#39;,&#39;Billing_Country&#39; as &#39;Billing_Country&#39;, 
avg(avg(&#39;Amount&#39;)) over([..] partition by &#39;all&#39;) as &#39;average&#39;,
avg(avg(&#39;Amount&#39;)) over([..] partition by &#39;all&#39;) + max(max(&#39;stddev&#39;)) over([..] partition by &#39;all&#39;) as &#39;+1STDDEV&#39;,
avg(avg(&#39;Amount&#39;)) over([..] partition by &#39;all&#39;) - max(max(&#39;stddev&#39;)) over([..] partition by &#39;all&#39;) as &#39;-1STDDEV&#39;;

