q = load &quot;simha__Wave_Sample_Opportunities3&quot;;
q = filter q by &#39;Won&#39; == &quot;Not Won&quot;;

q = group q by &#39;Industry&#39;;
q = foreach q generate &#39;Industry&#39; as &#39;Industry&#39;, count() as &#39;cnt&#39;,
count()/sum(count()) over ([..] partition by all )*100 as &#39;percent&#39;;
q = group q by &#39;Industry&#39;;
q = foreach q generate &#39;Industry&#39; as &#39;Industry&#39;,max(&#39;cnt&#39;) as &#39;count&#39;,
sum(sum(&#39;percent&#39;)) over ([..0] partition by all order by max(&#39;cnt&#39;) desc) as &#39;Cumulative %&#39;;
